export const useAlertasForm = (
  initialAlertas: IAlertas,
  dataService: IAlertasService
) => {
  const [data, setData] = useState<IAlertas>(initialAlertas);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadAlertas = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialAlertas);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchAlertasById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Alertas';
      setError(errorMessage);
      //console.log('Erro ao carregar Alertas');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialAlertas]);

  const resetForm = useCallback(() => {
    setData(initialAlertas);
    setError(null);
  }, [initialAlertas]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadAlertas,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadAlertas, resetForm]);
  return returnValue;
};


export const useAlertasNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Alertas', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        //console.log("Erro no listener de notificações.");
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


