export const useAndCompForm = (
  initialAndComp: IAndComp,
  dataService: IAndCompService
) => {
  const [data, setData] = useState<IAndComp>(initialAndComp);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadAndComp = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialAndComp);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchAndCompById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar And Comp';
      setError(errorMessage);
      //console.log('Erro ao carregar And Comp');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialAndComp]);

  const resetForm = useCallback(() => {
    setData(initialAndComp);
    setError(null);
  }, [initialAndComp]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadAndComp,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadAndComp, resetForm]);
  return returnValue;
};


export const useAndCompNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('AndComp', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        //console.log("Erro no listener de notificações.");
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


