export const useApensoForm = (
  initialApenso: IApenso,
  dataService: IApensoService
) => {
  const [data, setData] = useState<IApenso>(initialApenso);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadApenso = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialApenso);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchApensoById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Apenso';
      setError(errorMessage);
      //console.log('Erro ao carregar Apenso');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialApenso]);

  const resetForm = useCallback(() => {
    setData(initialApenso);
    setError(null);
  }, [initialApenso]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadApenso,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadApenso, resetForm]);
  return returnValue;
};


export const useApensoNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Apenso', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        //console.log("Erro no listener de notificações.");
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


export const useApensoList = (dataService: IApensoService, currentFilter?: FilterApenso | undefined | null) => {
  const [data, setData] = useState<IApenso[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null); 
  const currentFilterRef = useRef<FilterApenso | undefined | null>(currentFilter);

  useEffect(() => {
    currentFilterRef.current = currentFilter;
  }, [currentFilter]);

  const fetchData = useCallback(async (filtro?: FilterApenso | undefined | null) => {
   
    setLoading(true);
    setError(null);
    
    try {
      const result = await dataService.getAll(filtro ?? {} as FilterApenso);
      setData(result);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar apenso';
      setError(errorMessage);
      //console.log('Erro ao carregar apenso');
    } finally {
      setLoading(false);
    }
  }, [dataService]);   

 const refreshData = useCallback(() => {
     fetchData(currentFilterRef.current);
  }, [fetchData]);
  
  useApensoNotifications(
    refreshData, // onUpdate
    refreshData, // onDelete  
    refreshData  // onAdd
  );
   

  return {
    data,
    loading,
    error,
    fetchData,
    refreshData
  };
};


export function useValidationsApenso() {

  async function runValidation(data: IApenso, uri?: string, token?: string): Promise<{ isValid: boolean; message: string } | null> {

    const apensoApi = new ApensoApi(uri ?? '', token ?? '');

    const result = await apensoApi.validation(data);

    return result;
  }

  function validate(data: IApenso): { isValid: boolean; message: string } {
    if (!data) return { isValid: false, message: 'Dados não informados.' };
    
      try {
   
        if (data.apensox.length > 25) { 
                                             return { isValid: false, message: 'O campo Apenso não pode ter mais de 25 caracteres.' };
                                         } 
if (data.acao.length > 25) { 
                                             return { isValid: false, message: 'O campo Acao não pode ter mais de 25 caracteres.' };
                                         } 
if (data.obs.length > 2147483647) { 
                                             return { isValid: false, message: 'O campo OBS não pode ter mais de 2147483647 caracteres.' };
                                         } 



        return { isValid: true, message: '' };

         } catch (error) {
          return { isValid: true, message: 'Erro ao validar os dados.' };
    }

  }

 return { validate, runValidation };
}