export const useAtividadesForm = (
  initialAtividades: IAtividades,
  dataService: IAtividadesService
) => {
  const [data, setData] = useState<IAtividades>(initialAtividades);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadAtividades = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialAtividades);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchAtividadesById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Atividades';
      setError(errorMessage);
      //console.log('Erro ao carregar Atividades');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialAtividades]);

  const resetForm = useCallback(() => {
    setData(initialAtividades);
    setError(null);
  }, [initialAtividades]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadAtividades,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadAtividades, resetForm]);
  return returnValue;
};


export const useAtividadesNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Atividades', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        //console.log("Erro no listener de notificações.");
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


