export const useCidadeForm = (
  initialCidade: ICidade,
  dataService: ICidadeService
) => {
  const [data, setData] = useState<ICidade>(initialCidade);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadCidade = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialCidade);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchCidadeById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Cidade';
      setError(errorMessage);
      //console.log('Erro ao carregar Cidade');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialCidade]);

  const resetForm = useCallback(() => {
    setData(initialCidade);
    setError(null);
  }, [initialCidade]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadCidade,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadCidade, resetForm]);
  return returnValue;
};


export const useCidadeNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Cidade', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        //console.log("Erro no listener de notificações.");
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


