export const useDocumentosForm = (
  initialDocumentos: IDocumentos,
  dataService: IDocumentosService
) => {
  const [data, setData] = useState<IDocumentos>(initialDocumentos);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadDocumentos = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialDocumentos);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchDocumentosById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Documentos';
      setError(errorMessage);
      if (process.env.NEXT_PUBLIC_SHOW_LOG === '1')
        console.log('Erro ao carregar Documentos');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialDocumentos]);

  const resetForm = useCallback(() => {
    setData(initialDocumentos);
    setError(null);
  }, [initialDocumentos]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadDocumentos,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadDocumentos, resetForm]);
  return returnValue;
};


export const useDocumentosNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Documentos', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        if (process.env.NEXT_PUBLIC_SHOW_LOG === '1')
            console.log('Erro no listener de notificações.');
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


