export const useEndTitForm = (
  initialEndTit: IEndTit,
  dataService: IEndTitService
) => {
  const [data, setData] = useState<IEndTit>(initialEndTit);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadEndTit = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialEndTit);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchEndTitById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar End Tit';
      setError(errorMessage);
      //console.log('Erro ao carregar End Tit');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialEndTit]);

  const resetForm = useCallback(() => {
    setData(initialEndTit);
    setError(null);
  }, [initialEndTit]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadEndTit,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadEndTit, resetForm]);
  return returnValue;
};


