export const useGraphForm = (
  initialGraph: IGraph,
  dataService: IGraphService
) => {
  const [data, setData] = useState<IGraph>(initialGraph);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadGraph = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialGraph);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchGraphById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Graph';
      setError(errorMessage);
      if (process.env.NEXT_PUBLIC_SHOW_LOG === '1')
        console.log('Erro ao carregar Graph');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialGraph]);

  const resetForm = useCallback(() => {
    setData(initialGraph);
    setError(null);
  }, [initialGraph]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadGraph,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadGraph, resetForm]);
  return returnValue;
};


export const useGraphNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Graph', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        if (process.env.NEXT_PUBLIC_SHOW_LOG === '1')
            console.log('Erro no listener de notificações.');
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


export const useGraphList = (dataService: IGraphService, currentFilter?: FilterGraph | undefined | null) => {
  const [data, setData] = useState<IGraph[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null); 
  const currentFilterRef = useRef<FilterGraph | undefined | null>(currentFilter);

  useEffect(() => {
    currentFilterRef.current = currentFilter;
  }, [currentFilter]);

  const fetchData = useCallback(async (filtro?: FilterGraph | undefined | null) => {
   
    setLoading(true);
    setError(null);
    
    try {
      const result = await dataService.getAll(filtro ?? {} as FilterGraph);
      setData(result);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar graph';
      setError(errorMessage);
      if (process.env.NEXT_PUBLIC_SHOW_LOG === '1')
        console.log('Erro ao carregar graph');
    } finally {
      setLoading(false);
    }
  }, [dataService]);   

 const refreshData = useCallback(() => {
     fetchData(currentFilterRef.current);
  }, [fetchData]);
  
  useGraphNotifications(
    refreshData, // onUpdate
    refreshData, // onDelete  
    refreshData  // onAdd
  );
   

  return {
    data,
    loading,
    error,
    fetchData,
    refreshData
  };
};


export function useValidationsGraph() {

  async function runValidation(data: IGraph, uri?: string, token?: string): Promise<{ isValid: boolean; message: string } | null> {

    const graphApi = new GraphApi(uri ?? '', token ?? '');

    const result = await graphApi.validation(data);

    return result;
  }

  function validate(data: IGraph): { isValid: boolean; message: string } {
    if (!data) return { isValid: false, message: 'Dados não informados.' };
    
      try {
   
        if (data.tabela.length > 80) { 
                                             return { isValid: false, message: 'O campo Tabela não pode ter mais de 80 caracteres.' };
                                         } 



        return { isValid: true, message: '' };

         } catch (error) {
          return { isValid: true, message: 'Erro ao validar os dados.' };
    }

  }

 return { validate, runValidation };
}