export const useHistoricoForm = (
  initialHistorico: IHistorico,
  dataService: IHistoricoService
) => {
  const [data, setData] = useState<IHistorico>(initialHistorico);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadHistorico = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialHistorico);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchHistoricoById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Historico';
      setError(errorMessage);
      //console.log('Erro ao carregar Historico');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialHistorico]);

  const resetForm = useCallback(() => {
    setData(initialHistorico);
    setError(null);
  }, [initialHistorico]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadHistorico,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadHistorico, resetForm]);
  return returnValue;
};


export const useHistoricoNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Historico', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        //console.log("Erro no listener de notificações.");
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


export const useHistoricoList = (dataService: IHistoricoService, currentFilter?: FilterHistorico | undefined | null) => {
  const [data, setData] = useState<IHistorico[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null); 
  const currentFilterRef = useRef<FilterHistorico | undefined | null>(currentFilter);

  useEffect(() => {
    currentFilterRef.current = currentFilter;
  }, [currentFilter]);

  const fetchData = useCallback(async (filtro?: FilterHistorico | undefined | null) => {
   
    setLoading(true);
    setError(null);
    
    try {
      const result = await dataService.getAll(filtro ?? {} as FilterHistorico);
      setData(result);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar historico';
      setError(errorMessage);
      //console.log('Erro ao carregar historico');
    } finally {
      setLoading(false);
    }
  }, [dataService]);   

 const refreshData = useCallback(() => {
     fetchData(currentFilterRef.current);
  }, [fetchData]);
  
  useHistoricoNotifications(
    refreshData, // onUpdate
    refreshData, // onDelete  
    refreshData  // onAdd
  );
   

  return {
    data,
    loading,
    error,
    fetchData,
    refreshData
  };
};


export function useValidationsHistorico() {

  async function runValidation(data: IHistorico, uri?: string, token?: string): Promise<{ isValid: boolean; message: string } | null> {

    const historicoApi = new HistoricoApi(uri ?? '', token ?? '');

    const result = await historicoApi.validation(data);

    return result;
  }

  function validate(data: IHistorico): { isValid: boolean; message: string } {
    if (!data) return { isValid: false, message: 'Dados não informados.' };
    
      try {
   
        if (data.extraguid.length > 100) { 
                                             return { isValid: false, message: 'O campo ExtraGUID não pode ter mais de 100 caracteres.' };
                                         } 
if (data.observacao.length > 2147483647) { 
                                             return { isValid: false, message: 'O campo Observacao não pode ter mais de 2147483647 caracteres.' };
                                         } 



        return { isValid: true, message: '' };

         } catch (error) {
          return { isValid: true, message: 'Erro ao validar os dados.' };
    }

  }

 return { validate, runValidation };
}