export const useLigacoesForm = (
  initialLigacoes: ILigacoes,
  dataService: ILigacoesService
) => {
  const [data, setData] = useState<ILigacoes>(initialLigacoes);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadLigacoes = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialLigacoes);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchLigacoesById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Ligacoes';
      setError(errorMessage);
      //console.log('Erro ao carregar Ligacoes');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialLigacoes]);

  const resetForm = useCallback(() => {
    setData(initialLigacoes);
    setError(null);
  }, [initialLigacoes]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadLigacoes,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadLigacoes, resetForm]);
  return returnValue;
};


export const useLigacoesNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Ligacoes', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        //console.log("Erro no listener de notificações.");
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


