export const useOponentesForm = (
  initialOponentes: IOponentes,
  dataService: IOponentesService
) => {
  const [data, setData] = useState<IOponentes>(initialOponentes);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadOponentes = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialOponentes);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchOponentesById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Oponentes';
      setError(errorMessage);
      //console.log('Erro ao carregar Oponentes');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialOponentes]);

  const resetForm = useCallback(() => {
    setData(initialOponentes);
    setError(null);
  }, [initialOponentes]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadOponentes,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadOponentes, resetForm]);
  return returnValue;
};


export const useOponentesNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Oponentes', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        //console.log("Erro no listener de notificações.");
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


