export const usePenhoraForm = (
  initialPenhora: IPenhora,
  dataService: IPenhoraService
) => {
  const [data, setData] = useState<IPenhora>(initialPenhora);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadPenhora = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialPenhora);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchPenhoraById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Penhora';
      setError(errorMessage);
      if (process.env.NEXT_PUBLIC_SHOW_LOG === '1')
        console.log('Erro ao carregar Penhora');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialPenhora]);

  const resetForm = useCallback(() => {
    setData(initialPenhora);
    setError(null);
  }, [initialPenhora]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadPenhora,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadPenhora, resetForm]);
  return returnValue;
};


export const usePenhoraNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Penhora', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        if (process.env.NEXT_PUBLIC_SHOW_LOG === '1')
            console.log('Erro no listener de notificações.');
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


