export const useProPartesForm = (
  initialProPartes: IProPartes,
  dataService: IProPartesService
) => {
  const [data, setData] = useState<IProPartes>(initialProPartes);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadProPartes = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialProPartes);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchProPartesById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Pro Partes';
      setError(errorMessage);
      //console.log('Erro ao carregar Pro Partes');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialProPartes]);

  const resetForm = useCallback(() => {
    setData(initialProPartes);
    setError(null);
  }, [initialProPartes]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadProPartes,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadProPartes, resetForm]);
  return returnValue;
};


