export const useReuniaoForm = (
  initialReuniao: IReuniao,
  dataService: IReuniaoService
) => {
  const [data, setData] = useState<IReuniao>(initialReuniao);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadReuniao = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialReuniao);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchReuniaoById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Reunião';
      setError(errorMessage);
      //console.log('Erro ao carregar Reunião');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialReuniao]);

  const resetForm = useCallback(() => {
    setData(initialReuniao);
    setError(null);
  }, [initialReuniao]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadReuniao,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadReuniao, resetForm]);
  return returnValue;
};


export const useReuniaoNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Reuniao', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        //console.log("Erro no listener de notificações.");
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


export const useReuniaoList = (dataService: IReuniaoService, currentFilter?: FilterReuniao | undefined | null) => {
  const [data, setData] = useState<IReuniao[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null); 
  const currentFilterRef = useRef<FilterReuniao | undefined | null>(currentFilter);

  useEffect(() => {
    currentFilterRef.current = currentFilter;
  }, [currentFilter]);

  const fetchData = useCallback(async (filtro?: FilterReuniao | undefined | null) => {
   
    setLoading(true);
    setError(null);
    
    try {
      const result = await dataService.getAll(filtro ?? {} as FilterReuniao);
      setData(result);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar reuniao';
      setError(errorMessage);
      //console.log('Erro ao carregar reuniao');
    } finally {
      setLoading(false);
    }
  }, [dataService]);   

 const refreshData = useCallback(() => {
     fetchData(currentFilterRef.current);
  }, [fetchData]);
  
  useReuniaoNotifications(
    refreshData, // onUpdate
    refreshData, // onDelete  
    refreshData  // onAdd
  );
   

  return {
    data,
    loading,
    error,
    fetchData,
    refreshData
  };
};


