export const useSituacaoForm = (
  initialSituacao: ISituacao,
  dataService: ISituacaoService
) => {
  const [data, setData] = useState<ISituacao>(initialSituacao);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadSituacao = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialSituacao);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchSituacaoById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Situação';
      setError(errorMessage);
      if (process.env.NEXT_PUBLIC_SHOW_LOG === '1')
        console.log('Erro ao carregar Situação');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialSituacao]);

  const resetForm = useCallback(() => {
    setData(initialSituacao);
    setError(null);
  }, [initialSituacao]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadSituacao,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadSituacao, resetForm]);
  return returnValue;
};


