export const useTerceirosForm = (
  initialTerceiros: ITerceiros,
  dataService: ITerceirosService
) => {
  const [data, setData] = useState<ITerceiros>(initialTerceiros);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleChange = useCallback((e: any) => {
    const { name, value, type, checked } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));    
  }, []);

  const loadTerceiros = useCallback(async (id: number) => {
    if (!id || id === 0) {
      setData(initialTerceiros);
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      const dados = await dataService.fetchTerceirosById(id);
      setData(dados);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro ao carregar Terceiros';
      setError(errorMessage);
      //console.log('Erro ao carregar Terceiros');
    } finally {
      setLoading(false);
    }
  }, [dataService, initialTerceiros]);

  const resetForm = useCallback(() => {
    setData(initialTerceiros);
    setError(null);
  }, [initialTerceiros]);

  const returnValue = useMemo(() => ({
    data,
    loading,
    error,
    handleChange,
    loadTerceiros,
    resetForm,
    setData
  }), [data, loading, error, handleChange, loadTerceiros, resetForm]);
  return returnValue;
};


export const useTerceirosNotifications = (
  onUpdate?: (entity: any) => void,
  onDelete?: (entity: any) => void,
  onAdd?: (entity: any) => void
) => {
  const callbacksRef = useRef({ onUpdate, onDelete, onAdd });
  
  useEffect(() => {
    callbacksRef.current = { onUpdate, onDelete, onAdd };
  }, [onUpdate, onDelete, onAdd]);

  useEffect(() => {
    const unsubscribe = subscribeToNotifications('Terceiros', (entity) => {
      try {
        const { onUpdate, onDelete, onAdd } = callbacksRef.current;
        
        switch (entity.action) {
          case NotifySystemActions.DELETE:
            onDelete?.(entity);
            break;
          case NotifySystemActions.UPDATE:
            onUpdate?.(entity);
            break;
          case NotifySystemActions.ADD:
            onAdd?.(entity);
            break;
        }
      } catch (err) {
        //console.log("Erro no listener de notificações.");
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);
};


