        cWhere.Append(filtro.Quinta == int.MinValue ? string.Empty : (cWhere.Length == 0 ? string.Empty : filtro.LogicalOperator) + $"[{DBAgendaRepetirDicInfo.PTabelaNome}].[{DBAgendaRepetirDicInfo.Quinta}] = @{nameof(DBAgendaRepetirDicInfo.Quinta)}");
        cWhere.Append(filtro.Sexta == int.MinValue ? string.Empty : (cWhere.Length == 0 ? string.Empty : filtro.LogicalOperator) + $"[{DBAgendaRepetirDicInfo.PTabelaNome}].[{DBAgendaRepetirDicInfo.Sexta}] = @{nameof(DBAgendaRepetirDicInfo.Sexta)}");
        cWhere.Append(filtro.Sabado == int.MinValue ? string.Empty : (cWhere.Length == 0 ? string.Empty : filtro.LogicalOperator) + $"[{DBAgendaRepetirDicInfo.PTabelaNome}].[{DBAgendaRepetirDicInfo.Sabado}] = @{nameof(DBAgendaRepetirDicInfo.Sabado)}");
        cWhere.Append(filtro.Domingo == int.MinValue ? string.Empty : (cWhere.Length == 0 ? string.Empty : filtro.LogicalOperator) + $"[{DBAgendaRepetirDicInfo.PTabelaNome}].[{DBAgendaRepetirDicInfo.Domingo}] = @{nameof(DBAgendaRepetirDicInfo.Domingo)}");
        cWhere.Append(filtro.Terca == int.MinValue ? string.Empty : (cWhere.Length == 0 ? string.Empty : filtro.LogicalOperator) + $"[{DBAgendaRepetirDicInfo.PTabelaNome}].[{DBAgendaRepetirDicInfo.Terca}] = @{nameof(DBAgendaRepetirDicInfo.Terca)}");
        if (!filtro.Codigo_filtro.IsEmpty() && filtro.Codigo_filtro_end.IsEmpty())
        {
            cWhere.Append(filtro.Codigo_filtro <= 0 ? string.Empty : (cWhere.Length == 0 ? string.Empty : filtro.LogicalOperator) + $"[{DBAgendaRepetirDicInfo.PTabelaNome}].[{DBAgendaRepetirDicInfo.CampoCodigo}] >= @{nameof(DBAgendaRepetirDicInfo.CampoCodigo)}");
        }
        else
        {
            cWhere.Append((filtro.Codigo_filtro <= 0 && filtro.Codigo_filtro_end <= 0) ? string.Empty : (!(filtro.Codigo_filtro <= 0) && !(filtro.Codigo_filtro_end <= 0)) ? (cWhere.Length == 0 ? string.Empty : filtro.LogicalOperator) + $"{DBAgendaRepetirDicInfo.CampoCodigo} BETWEEN @{nameof(DBAgendaRepetirDicInfo.CampoCodigo)} AND @{nameof(DBAgendaRepetirDicInfo.CampoCodigo)}_end" : !(filtro.Codigo_filtro <= 0) ? (cWhere.Length == 0 ? string.Empty : filtro.LogicalOperator) + $"{DBAgendaRepetirDicInfo.CampoCodigo} = @{nameof(DBAgendaRepetirDicInfo.CampoCodigo)}" : (cWhere.Length == 0 ? string.Empty : filtro.LogicalOperator) + $"{DBAgendaRepetirDicInfo.CampoCodigo} <= @{nameof(DBAgendaRepetirDicInfo.CampoCodigo)}_end");
        }

        return (cWhere.ToString().Trim(), parameters);
    }

    private static string ApplyWildCard(char wildcardChar, string value)
    {
        if (wildcardChar == '\0' || wildcardChar == ' ')
        {
            return value;
        }

        var result = $"{wildcardChar}{value.Replace(" ", wildcardChar.ToString())}{wildcardChar}";
        return result;
    }

    private async Task<IEnumerable<AgendaRepetirResponseAll>> GetDataAllAsync(int max, string where, List<SqlParameter> parameters, string uri, CancellationToken token)
    {
        using var oCnn = Configuracoes.GetConnectionByUri(uri);
        if (oCnn == null)
        {
            throw new DatabaseConnectionException();
        }

        var result = await reader.Listar(max, uri, where, parameters, string.Empty, token);
        return result;
    }
