            resultado.Value.where.Should().Contain($"@{DBAcaoDicInfo.Descricao}");
            resultado.Value.where.Should().Contain(" AND ");
            // Deve ter 2 parâmetro(s)
            resultado.Value.parametros.Should().HaveCount(2);
        }

