        [Fact]
        public void WFiltro_HoraSaida_WhenValidDateRange_ShouldAddBothParametersAndGenerateBetweenClause()
        {
            // Arrange
            var startDate = "01/06/2023";
            var endDate = "30/06/2023";
            var filtro = new FilterPontoVirtual
            {
                HoraSaida = startDate,
                HoraSaida_end = endDate,
                LogicalOperator = TSql.And
            };
            // Act
            var result = _service.WFiltro(filtro);
            // Assert
            result.Should().NotBeNull();
            result.Value.parametros.Should().HaveCount(2);
            var startParameter = result.Value.parametros.FirstOrDefault(p => p.ParameterName == $"@{DBPontoVirtualDicInfo.HoraSaida}");
            var endParameter = result.Value.parametros.FirstOrDefault(p => p.ParameterName == $"@{DBPontoVirtualDicInfo.HoraSaida}_end");
            startParameter.Should().NotBeNull();
            endParameter.Should().NotBeNull();
            startParameter!.Value.Should().BeOfType<DateTime>();
            endParameter!.Value.Should().BeOfType<DateTime>();
            ((DateTime)startParameter.Value).Should().Be(DateTime.Parse(startDate));
            ((DateTime)endParameter.Value).Should().Be(DateTime.Parse(endDate));
            result.Value.where.Should().Contain($"[{DBPontoVirtualDicInfo.PTabelaNome}].{DBPontoVirtualDicInfo.HoraSaida} BETWEEN @{DBPontoVirtualDicInfo.HoraSaida} AND @{DBPontoVirtualDicInfo.HoraSaida}_end");
