        {
            // Arrange
            var TipoValue = 1;
            var filtro = new FilterPontoVirtualAcessos
            {
                Tipo = TipoValue,
                LogicalOperator = " AND "
            };
            // Act
            var result = _service.WFiltro(filtro);
            // Assert
            result.Should().NotBeNull();
            // Check parameter
            var TipoParameter = result.Value.parametros.FirstOrDefault(p => p.ParameterName == $"@{DBPontoVirtualAcessosDicInfo.Tipo}");
            TipoParameter.Should().NotBeNull();
            TipoParameter.Value.Should().Be(TipoValue);
            // Check where clause
            result.Value.where.Should().Contain($"[{DBPontoVirtualAcessosDicInfo.PTabelaNome}].[{DBPontoVirtualAcessosDicInfo.Tipo}] = @{DBPontoVirtualAcessosDicInfo.Tipo}");
        }

        [Fact]
