            // Assert
            resultado.Should().NotBeNull();
            // Deve usar operador BETWEEN
            resultado.Value.where.Should().Contain($"[{DBProcessosObsReportDicInfo.PTabelaNome}].{DBProcessosObsReportDicInfo.Processo} BETWEEN @{DBProcessosObsReportDicInfo.Processo} AND @{DBProcessosObsReportDicInfo.Processo}_end");
