        [Fact]
        public void WFiltro_TopHasValidValue_ShouldAddParameterAndWhereClause()
        {
            // Arrange
            var TopValue = 1;
            var filtro = new FilterSituacao
            {
                Top = TopValue,
                LogicalOperator = " AND "
            };
            // Act
            var result = _service.WFiltro(filtro);
            // Assert
            result.Should().NotBeNull();
            // Check parameter
            var TopParameter = result.Value.parametros.FirstOrDefault(p => p.ParameterName == $"@{DBSituacaoDicInfo.Top}");
            TopParameter.Should().NotBeNull();
            TopParameter.Value.Should().Be(TopValue);
            // Check where clause
            result.Value.where.Should().Contain($"[{DBSituacaoDicInfo.PTabelaNome}].[{DBSituacaoDicInfo.Top}] = @{DBSituacaoDicInfo.Top}");
        }

        [Fact]
        public void WFiltro_TopWithDifferentValues_ShouldAddCorrectParameterValue()
        {
            // Arrange & Act & Assert for Top = 0 (typically female)
            var filtroFemale = new FilterSituacao
            {
                Top = 0
            };
            var resultFemale = _service.WFiltro(filtroFemale);
            resultFemale.Should().NotBeNull();
            var femaleParam = resultFemale.Value.parametros.FirstOrDefault(p => p.ParameterName == $"@{DBSituacaoDicInfo.Top}");
            femaleParam.Should().NotBeNull();
            femaleParam.Value.Should().Be(0);
            // Arrange & Act & Assert for Top = 1 (typically male)
            var filtroMale = new FilterSituacao
            {
                Top = 1
            };
            var resultMale = _service.WFiltro(filtroMale);
            resultMale.Should().NotBeNull();
            var maleParam = resultMale.Value.parametros.FirstOrDefault(p => p.ParameterName == $"@{DBSituacaoDicInfo.Top}");
            maleParam.Should().NotBeNull();
            maleParam.Value.Should().Be(1);
        }

